import React, { useState } from 'react';
import styled from 'styled-components';
import { Waypoint } from 'react-waypoint';
import media from 'styles/media';
import NameModal from '../NameModal';
import DownArrow from './DownArrow';

const Container = styled.div`
  align-items: center;
  display: flex;
  height: calc(100% - 100px);
  justify-content: center;
`;

const TextContainer = styled.div`
  color: White;
  font-family: 'Space Mono', monospace;
  margin-top: -5%;
  margin: 0 1.5em 0 1.5em;
  text-align: center;
`;

const ColorSpan = styled.span`
  color: ${({ theme }) => theme.colors.primary};
`;

const Background = styled.div`
  height: 100%;
  left: 0;
  position: absolute;
  top: 0;
  width: 100vw;
  z-index: -1;
  background-color: black;
`;


const H1 = styled.h1`
  ${media.phone`font-size: 1.6em;`}
`;

function About() {
  const [arrowVisible, setArrowVisible] = useState(true);
  const [nudgeNeeded, setNudgeNeeded] = useState(true);
  const [nameModalOpen, setNameModalOpen] = useState(false);

  return (
    <>
      <Background>
      </Background>
      <Container id="about">
        <TextContainer>
          <Waypoint
            onPositionChange={({ currentPosition }) => {
              setArrowVisible(currentPosition === 'inside');
            }}
          />
          <H1>
            Iâ€™m <ColorSpan>Peter Tyldesley</ColorSpan>, full-stack developer,
            <br />
            full-time tinkerer and creator of things. ðŸ‘Š
          </H1>
        </TextContainer>
      </Container>
      <DownArrow visible={arrowVisible} />
      <NameModal open={nameModalOpen} onClose={() => setNameModalOpen(false)} />
    </>
  );
}

export default About;
